void pattern(int n)
{
    int i, j です。

    // 行数を処理するための外周ループ
    for (i = 0; i < n; i++) {。

        // カラム数を処理するための内部ループ
        for (j = 0; j <= i; j++) {。
            // 星を刷る
            cout << "* ";
        }

        // 各行の後の終了行
